//驗證碼的"響應"資料類型規範
interface CaptchaAPIRes {
  msg: string;
  img: string;
  code: number;
  captchaEnabled: boolean;
  uuid: string;
}

// 登入"請求"資料型態規範
interface LoginAPIReq {
    username:string;
    password:string;
    code:string;
    uuid:string;
}

// 登入"響應"資料型態規範
interface LoginAPIRes {
    msg:string;
    code:number;
    token:string;
}